import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ScatterPlot from './ScatterPlot';
import * as d3 from 'd3';


// Region color map (shared by legend and chart)
// const regionColorMap = {
//   Africa: '#8c564b',
//   Asia: '#17becf',
//   Europe: '#1f77b4',
//   'North America': '#ff7f0e',
//   Oceania: '#2ca02c',
//   'South America': '#d62728'
// };
// const regionList = Object.keys(regionColorMap);

const regionList = ['Africa', 'Asia', 'Europe', 'North America', 'Oceania', 'South America'];

const regionColorMap = {};
regionList.forEach((region, i) => {
  regionColorMap[region] = d3.schemeTableau10[i]; // Assign unique color from the palette
});


const Home = () => {
  const [data, setData] = useState([]);
  const [selectedYear, setSelectedYear] = useState(2022);
  const [yearRange, setYearRange] = useState({ min: 1950, max: 2022 });

  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then(response => {
        setData(response.data);

        const years = response.data.map(d => d.year);
        const minYear = 1800; // Hardcoded if desired
        const maxYear = 2022;
        setYearRange({ min: minYear, max: maxYear });
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>GDP vs Pollution Visualization</h1>

      {/* Year slider */}
      <div style={{ marginBottom: '30px' }}>
        <label htmlFor="yearSlider">Year: {selectedYear}</label><br />
        <input
          id="yearSlider"
          type="range"
          min={yearRange.min}
          max={yearRange.max}
          value={selectedYear}
          onChange={(e) => setSelectedYear(parseInt(e.target.value))}
          style={{ width: '400px' }}
        />
      </div>

      {/* Region Color Legend */}
      <div style={{ marginBottom: '30px' }}>
        <strong>Region Legend:</strong>
        <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '10px' }}>
          {regionList.map(region => (
            <div key={region} style={{ display: 'flex', alignItems: 'center', marginRight: '20px' }}>
              <div style={{
                width: '12px',
                height: '12px',
                borderRadius: '50%',
                backgroundColor: regionColorMap[region],
                marginRight: '6px'
              }}></div>
              <span>{region}</span>
            </div>
          ))}
        </div>
      </div>

      {/* D3 Chart */}
      <ScatterPlot data={data} selectedYear={selectedYear} regionColorMap={regionColorMap} />
    </div>
  );
};

export default Home;
