import React, { useEffect, useRef, useState } from 'react';

import * as d3 from 'd3';

const ScatterPlot = ({ data, selectedYear = 2022, regionColorMap, activeRegions,selectedCountry,
  setSelectedCountry }) => {
  const svgRef = useRef();

  useEffect(() => {
    if (!data.length) return;

   // const filteredData = data.filter(d => d.year === selectedYear);
   const filteredData = data.filter(d =>
    d.year === selectedYear &&
    d.entity !== 'World' &&
    !d.entity.toLowerCase().includes('income') &&
    d.gdp_per_capita > 0 &&
    d.co2_emissions_per_capita > 0 &&
    d.population > 0
  );

  
  
    const width = 900;
    const height = 550;
    const margin = { top: 40, right: 40, bottom: 60, left: 80 };

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove(); // Clear old content

    const chart = svg
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    // Scales
    const xScale = d3.scaleLog()
      .domain([100, d3.max(filteredData, d => d.gdp_per_capita || 100000)])
      .range([0, innerWidth]);

    const yScale = d3.scaleLog()
      .domain([0.1, d3.max(filteredData, d => d.co2_emissions_per_capita || 10)])
      .range([innerHeight, 0]);

    // const regionColors = d3.scaleOrdinal()
    //   .domain(['Africa', 'Asia', 'Europe', 'North America', 'Oceania', 'South America'])
    //   .range(['#8c564b', '#17becf', '#1f77b4', '#ff7f0e', '#2ca02c', '#d62728']);

    // Axes
    chart.append('g')
      .attr('transform', `translate(0, ${innerHeight})`)
      .call(d3.axisBottom(xScale).ticks(10, "~s"))
      .append('text')
      .attr('x', innerWidth / 2)
      .attr('y', 40)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('GDP per capita (log scale)');

    chart.append('g')
      .call(d3.axisLeft(yScale).ticks(10, "~s"))
      .append('text')
      .attr('transform', 'rotate(-90)')
      .attr('x', -innerHeight / 2)
      .attr('y', -60)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('CO₂ emissions per capita (log scale)');

    // Tooltip div
    const tooltip = d3.select("body")
      .append("div")
      .style("position", "absolute")
      .style("background", "#fff")
      .style("padding", "6px 10px")
      .style("border", "1px solid #ccc")
      .style("border-radius", "4px")
      .style("pointer-events", "none")
      .style("opacity", 0)
      .on('click', (event) => {
        event.stopPropagation(); // Prevent sticky tooltip behavior
      });;

 

    // Remove previous circles
chart.selectAll('circle.bubble').remove();
chart.selectAll('circle.ring').remove();

// RING CIRCLES (outer)
chart.selectAll('circle.ring')
  .data(filteredData.filter(d => activeRegions.includes(d.region)))
  .enter()
  .append('circle')
  .attr('class', 'ring')
  .attr('cx', d => xScale(d.gdp_per_capita || 100))
  .attr('cy', d => yScale(d.co2_emissions_per_capita || 0.1))
  .attr('r', d => Math.sqrt(d.population) / 1000 + 4) // slightly bigger than main circle
  .attr('fill', 'none')
  .attr('stroke', d => regionColorMap[d.region] || '#000')
  .attr('stroke-width', 2)
  .attr('opacity', 1);

// MAIN CIRCLES (data bubble)
chart.selectAll('circle.bubble')
  .data(filteredData)
  .enter()
  .append('circle')
  .attr('class', 'bubble')
  .attr('cx', d => xScale(d.gdp_per_capita || 100))
  .attr('cy', d => yScale(d.co2_emissions_per_capita || 0.1))
  .attr('r', d => Math.sqrt(d.population) / 1000)
  .attr('stroke', d => selectedCountry?.entity === d.entity ? regionColorMap[d.region] : 'none')
.attr('stroke-width', d => selectedCountry?.entity === d.entity ? 3 : 0)
.attr('r', d => {
  const baseR = Math.sqrt(d.population) / 1000;
  return selectedCountry?.entity === d.entity ? baseR + 4 : baseR;
})

  .attr('fill', d =>
    activeRegions.includes(d.region)
      ? regionColorMap[d.region] || '#000'
      : '#ccc'
  )
  .attr('opacity', 0.85)
  .on('mouseover', function (event, d) {
    if (!selectedCountry) {
      tooltip.transition().duration(200).style('opacity', 1);
      tooltip.html(`
        <strong>${d.entity}</strong><br/>
        Year: ${d.year}<br/>
        GDP per capita: $${Math.round(d.gdp_per_capita)}<br/>
        CO₂ per capita: ${d.co2_emissions_per_capita.toFixed(2)} t<br/>
        Population: ${d.population.toLocaleString()}
      `)
        .style("left", (event.pageX + 10) + "px")
        .style("top", (event.pageY - 28) + "px");
    }
    
    // tooltip.transition().duration(200).style('opacity', 1);
    // tooltip.html(`
    //   <strong>${d.entity}</strong><br/>
    //   Year: ${d.year}<br/>
    //   GDP per capita: $${Math.round(d.gdp_per_capita)}<br/>
    //   CO₂ per capita: ${d.co2_emissions_per_capita.toFixed(2)} t<br/>
    //   Population: ${d.population.toLocaleString()}
    // `)
    //   .style("left", (event.pageX + 10) + "px")
    //   .style("top", (event.pageY - 28) + "px");
  
    // // Highlight hovered bubble
    // d3.select(this)
    //   .attr('stroke', regionColorMap[d.region] || '#000')
    //   .attr('stroke-width', 3)
    //   .attr('fill', d3.color(regionColorMap[d.region]).brighter(0.5))
    //   .attr('r', Math.sqrt(d.population) / 1000 + 4); // Slight size increase
  }
)
  
.on('mouseout', function (event, d) {
  if (!selectedCountry) {
    tooltip.transition().duration(500).style('opacity', 0);
  }

  d3.select(this)
    .attr('stroke', selectedCountry?.entity === d.entity ? regionColorMap[d.region] : 'none')
    .attr('r', Math.sqrt(d.population) / 1000)
    .attr('fill', d =>
      activeRegions.includes(d.region)
        ? regionColorMap[d.region] || '#000'
        : '#ccc'
    );
});
  

chart.selectAll('text.label').remove();

// Add country labels
chart.selectAll('text.label')
  .data(filteredData.filter(d => activeRegions.includes(d.region))) // Only for selected regions
  .enter()
  .append('text')
  .attr('class', 'label')
  .attr('x', d => xScale(d.gdp_per_capita || 100))
  .attr('y', d => yScale(d.co2_emissions_per_capita || 0.1) - 8) // Above the circle
  .text(d => d.entity)
  .attr('font-size', '10px')
  .attr('fill', d => regionColorMap[d.region] || '#333')
  .attr('font-weight', 'bold')
  .attr('text-anchor', 'middle');



  }, [data, selectedYear, activeRegions, regionColorMap]);

  return <svg ref={svgRef}></svg>;
};

export default ScatterPlot;
