import React from 'react';
import * as d3 from 'd3';

const chartWidth = 220;
const chartHeight = 130;
const margin = { top: 20, right: 10, bottom: 20, left: 40 };

const metrics = [
  { key: 'co2', label: 'CO₂ per Capita (t)' },
  { key: 'gdp', label: 'GDP per Capita ($)' },
  { key: 'population', label: 'Population' }
];

const MultiTrendCharts = ({ selectedCountries, trendMap, regionColorMap }) => {
  if (selectedCountries.length === 0) return null;

  // Shared X scale (assume all countries have same year range)
  const sampleData = trendMap[selectedCountries[0].entity] || [];
  const years = sampleData.map(d => d.year);
  const xScale = d3.scaleLinear()
    .domain(d3.extent(years))
    .range([margin.left, chartWidth - margin.right]);

  return (
    <div style={{ display: 'flex', gap: '40px', marginTop: '30px' }}>
      {selectedCountries.map(country => (
        <div key={country.entity}>
          <div style={{ fontWeight: 'bold', marginBottom: '10px', textAlign: 'center' }}>
            {country.entity}
          </div>

          {metrics.map(metric => {
            const data = (trendMap[country.entity] || []).filter(
              d => d.year >= 1850 && d[metric.key] !== null && d[metric.key] > 0
            );
            if (data.length === 0) return null;

            const yMax = d3.max(data, d => d[metric.key]);
            const yScale = d3.scaleLinear()
              .domain([0, yMax])
              .nice()
              .range([chartHeight - margin.bottom, margin.top]);

            const line = d3.line()
              .x(d => xScale(d.year))
              .y(d => yScale(d[metric.key]));

            return (
              <svg
                key={metric.key}
                width={chartWidth}
                height={chartHeight}
                style={{ display: 'block', marginBottom: '14px' }}
              >
                {/* Grid Lines + Y Ticks */}
                {yScale.ticks(3).map((tick, i) => (
                  <g key={i}>
                    <text
                      x={margin.left - 6}
                      y={yScale(tick)}
                      fontSize="10"
                      textAnchor="end"
                      alignmentBaseline="middle"
                      fill="#444"
                    >
                      {tick.toLocaleString()}
                    </text>
                    <line
                      x1={margin.left}
                      x2={chartWidth - margin.right}
                      y1={yScale(tick)}
                      y2={yScale(tick)}
                      stroke="#ccc"
                      strokeDasharray="2 2"
                    />
                  </g>
                ))}

                {/* Title */}
                <text
                  x={chartWidth / 2}
                  y={12}
                  textAnchor="middle"
                  fontSize="11"
                  fill="#333"
                >
                  {metric.label}
                </text>

                {/* Data Line */}
                <path
                  d={line(data)}
                  fill="none"
                  stroke={regionColorMap[country.region] || '#000'}
                  strokeWidth={2}
                />
              </svg>
            );
          })}
        </div>
      ))}
    </div>
  );
};

export default MultiTrendCharts;
