import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import ScatterPlot from './ScatterPlot';
import CountrySelector from './CountrySelector';
import MultiTrendCharts from './MultiTrendCharts'; // 👈 NEW
import * as d3 from 'd3';

const regionList = ['Africa', 'Asia', 'Europe', 'North America', 'Oceania', 'South America'];

const regionColorMap = {};
regionList.forEach((region, i) => {
  regionColorMap[region] = d3.schemeTableau10[i];
});

const Home = () => {
  const [data, setData] = useState([]);
  const [selectedYear, setSelectedYear] = useState(2022);
  const [yearRange, setYearRange] = useState({ min: 1950, max: 2022 });
  const [activeRegions, setActiveRegions] = useState([]);
  const [selectedCountries, setSelectedCountries] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [intervalId, setIntervalId] = useState(null);
  const [showCountrySelector, setShowCountrySelector] = useState(false);
  const [zoomMode, setZoomMode] = useState(false);
  const [hoveredCountry, setHoveredCountry] = useState(null);
  const [hoverPos, setHoverPos] = useState({ x: 0, y: 0 });

  // Expanded trend map with all 3 metrics
  const countryTrendMap = useMemo(() => {
    const map = {};
    data.forEach(d => {
      if (!map[d.entity]) map[d.entity] = [];
      map[d.entity].push({
        year: d.year,
        co2: d.co2_emissions_per_capita,
        gdp: d.gdp_per_capita,
        population: d.population
      });
    });
    return map;
  }, [data]);

  const startAnimation = () => {
    if (!isPlaying) {
      const id = setInterval(() => {
        setSelectedYear(prev => {
          const nextYear = prev + 1;
          if (nextYear > yearRange.max) {
            clearInterval(id);
            setIsPlaying(false);
            return yearRange.max;
          }
          return nextYear;
        });
      }, 100);
      setIntervalId(id);
      setIsPlaying(true);
    }
  };

  const stopAnimation = () => {
    clearInterval(intervalId);
    setIsPlaying(false);
  };

  const toggleAnimation = () => {
    isPlaying ? stopAnimation() : startAnimation();
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then(response => {
        setData(response.data);
        setYearRange({ min: 1800, max: 2022 });
      })
      .catch(error => console.error('Error fetching data:', error));

    return () => clearInterval(intervalId);
  }, []);

  const toggleRegion = (region) => {
    setActiveRegions(prev =>
      prev.includes(region)
        ? prev.filter(r => r !== region)
        : [...prev, region]
    );
  };
  const toggleCountry = (country) => {
    setSelectedCountries(prev => {
      const exists = prev.find(c => c.entity === country.entity);
      
      if (exists) {
        // Deselect country
        return prev.filter(c => c.entity !== country.entity);
      } else if (prev.length < 3) {
        // Select new country (if less than 3)
        return [...prev, country];
      } else {
        // Already 3 selected — block selection
        alert('You can only compare up to 3 countries.');
        return prev; // no change
      }
    });
  };
  
  

  return (
    <div style={{ padding: '20px', position: 'relative' }}>
      <h1>GDP vs Pollution Visualization</h1>

      {/* Horizontal Legend */}
      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px',
        marginBottom: '20px'
      }}>
        {regionList.map(region => (
          <label key={region} style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
            <input
              type="checkbox"
              checked={activeRegions.includes(region)}
              onChange={() => toggleRegion(region)}
              style={{
                appearance: 'none',
                WebkitAppearance: 'none',
                width: '18px',
                height: '18px',
                borderRadius: '4px',
                backgroundColor: activeRegions.includes(region)
                  ? regionColorMap[region]
                  : '#fff',
                border: `2px solid ${regionColorMap[region]}`,
                marginRight: '8px',
                cursor: 'pointer'
              }}
            />
            <span>{region}</span>
          </label>
        ))}
      </div>

      {/* Controls */}
      <div style={{ marginTop: '20px', display: 'flex', flexDirection: 'row', gap: '10px' }}>
        <button
          onClick={() => setZoomMode(!zoomMode)}
          style={{
            padding: '8px 12px',
            backgroundColor: zoomMode ? '#007bff' : '#ccc',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {zoomMode ? '🧭 Zoom Mode: ON' : '🔍 Enable Zoom Mode'}
        </button>
        <button onClick={() => setShowCountrySelector(true)}>+ Select Countries</button>
        <button
          onClick={() => {
            setSelectedCountries([]);
            setActiveRegions([]);
          }}
          style={{
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            padding: '8px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Reset Selections
        </button>
      </div>

     
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
  {/* ScatterPlot */}
  <ScatterPlot
    data={data}
    selectedYear={selectedYear}
    regionColorMap={regionColorMap}
    activeRegions={activeRegions}
    selectedCountries={selectedCountries}
    setSelectedCountries={setSelectedCountries}
    zoomMode={zoomMode}
    setHoveredCountry={setHoveredCountry}
    setHoverPos={setHoverPos}
  />

  {/* Timeline (now directly under the chart) */}
  <div style={{
    display: 'flex',
    justifyContent: 'center',
    width: '100%',
    margin: '20px auto 10px auto',
    gap: '10px'
  }}>
    <button
      onClick={toggleAnimation}
      style={{
        width: '40px',
        padding: '8px',
        fontSize: '14px',
        color: 'grey',
        border: '1px solid #999',
        borderRadius: '4px',
        cursor: 'pointer'
      }}
    >
      {isPlaying ? '❚❚' : '▶'}
    </button>

    <input
      type="range"
      min={yearRange.min}
      max={yearRange.max}
      value={selectedYear}
      onChange={(e) => setSelectedYear(parseInt(e.target.value))}
      style={{
        flex: 1,
        height: '6px',
        background: '#ccc',
        borderRadius: '3px',
        appearance: 'none',
        WebkitAppearance: 'none',
        outline: 'none',
        cursor: 'pointer',
        accentColor: 'grey'
      }}
    />
  </div>

  {/* Year Label */}
  <div style={{ textAlign: 'center', fontSize: '16px', color: '#444' }}>
    Year: {selectedYear}
  </div>
</div>


      {/* Country Selector Panel */}
      {showCountrySelector && (
        <CountrySelector
          data={data}
          selectedCountries={selectedCountries}
          toggleCountry={toggleCountry}
          close={() => setShowCountrySelector(false)}
        />
      )}
    </div>
  );
};

export default Home;
