import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const ScatterPlot = ({
  data,
  selectedYear = 2022,
  regionColorMap,
  activeRegions,
  selectedCountries,
  setSelectedCountries,
  zoomMode,
  setHoveredCountry,         
  setHoverTrendData  ,
  setHoverPos
}) => {
  const svgRef = useRef();
  const [zoomExtent, setZoomExtent] = useState(null);
  //const [hoveredCountry, setHoveredCountry] = useState(null);
  

  useEffect(() => {
    if (!data.length) return;

    const filteredData = data.filter(d =>
      d.year === selectedYear &&
      d.entity !== 'World' &&
      !d.entity.toLowerCase().includes('income') &&
      d.gdp_per_capita > 0 &&
      d.co2_emissions_per_capita > 0 &&
      d.population > 0
    );

    const width = 900;
    const height = 450;
    const margin = { top: 40, right: 40, bottom: 60, left: 80 };

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const chart = svg
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

// Scales
let x = d3.scaleLog()
  .domain([50, d3.max(filteredData, d => d.gdp_per_capita || 100000)])
  .range([0, innerWidth]);

let y = d3.scaleLog()
  .domain([0.02, d3.max(filteredData, d => d.co2_emissions_per_capita || 10)])
  .range([innerHeight, 0]);

if (zoomExtent) {
  x = d3.scaleLog()
    .domain([x.invert(zoomExtent[0][0]), x.invert(zoomExtent[1][0])])
    .range([0, innerWidth]);

  y = d3.scaleLog()
    .domain([y.invert(zoomExtent[1][1]), y.invert(zoomExtent[0][1])])
    .range([innerHeight, 0]);
}

// Compute tick values for Y (just major log values)
const maxY = d3.max(filteredData, d => d.co2_emissions_per_capita || 10);
const yTickValues = [0.1, 1, 10, 30].filter(val => val <= maxY);
const xTickValues = [100, 1_000, 5_000, 20_000, 100_000];


// Axes
chart.append('g')
  .attr('transform', `translate(0, ${innerHeight})`)
  .call(d3.axisBottom(x).ticks(10, "~s"))
  .append('text')
  .attr('x', innerWidth / 2)
  .attr('y', 40)
  .attr('fill', '#000')
  .attr('text-anchor', 'middle')
  .text('GDP per capita (log scale)');

// Horizontal Grid Lines (Y-axis)
chart.append('g')
  .attr('class', 'grid')
  .call(d3.axisLeft(y)
    .tickValues(yTickValues)        // ✅ Only major ticks
    .tickSize(-innerWidth)
    .tickFormat('')                 // Hide labels (grid only)
  )
  .selectAll('line')
  .attr('stroke', '#ccc')
  .attr('stroke-dasharray', '3 3');


    chart.append('g')
      .call(d3.axisLeft(y).ticks(10, "~s"))
      .append('text')
      .attr('transform', 'rotate(-90)')
      .attr('x', -innerHeight / 2)
      .attr('y', -60)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('CO₂ emissions per capita (log scale)');

      chart.selectAll('.grid path.domain').remove();

      chart.append('g')
  .attr('class', 'x-grid')
  .attr('transform', `translate(0, ${innerHeight})`)
  .call(
    d3.axisBottom(x)
    .tickValues(xTickValues)
      .tickSize(-innerHeight)   // Full-height vertical lines
      .tickFormat('')           // No text labels
  )
  .selectAll('line')
  
  .attr('stroke', '#ccc')
  .attr('stroke-dasharray', '3 3');
  chart.selectAll('.x-grid path.domain').remove();
    // Bubbles
    chart.selectAll('circle.bubble')
      .data(filteredData)
      .enter()
      .append('circle')
      .attr('class', 'bubble')
      .attr('cx', d => x(d.gdp_per_capita))
      .attr('cy', d => y(d.co2_emissions_per_capita))
      .attr('r', d => {
        const baseR = Math.sqrt(d.population) / 1000;
        return selectedCountries.some(c => c.entity === d.entity) ? baseR + 4 : baseR;
      })
      .attr('fill', d => {
        if (selectedCountries.some(c => c.entity === d.entity)) {
          return d3.color(regionColorMap[d.region]).brighter(0.5);
        }
        return activeRegions.includes(d.region)
          ? regionColorMap[d.region] || '#000'
          : '#ccc';
      })
      .attr('stroke', d =>
        selectedCountries.some(c => c.entity === d.entity)
          ? regionColorMap[d.region]
          : 'none'
      )
      .attr('stroke-width', d =>
        selectedCountries.some(c => c.entity === d.entity) ? 3 : 0
      )
      .attr('opacity', 0.85)
      .on('mouseover', function (event, d) {
        if (!zoomMode) {
          setHoveredCountry(d);
 // setHoverTrendData(d.entity);
          setHoveredCountry(d);
         // setHoverPos({ x: event.pageX + 10, y: event.pageY - 28 });
         const svgRect = svgRef.current.getBoundingClientRect();
         setHoverPos({
           x: event.clientX - svgRect.left + 10,
           y: event.clientY - svgRect.top - 28
         });
          d3.select(this)
            .attr('stroke', regionColorMap[d.region])
            .attr('stroke-width', 3)
            .attr('fill', d3.color(regionColorMap[d.region]).brighter(0.5))
            .attr('r', Math.sqrt(d.population) / 1000 + 4);
        }
      })
      .on('mouseout', function (event, d) {
        setHoveredCountry(null);

        const baseR = Math.sqrt(d.population) / 1000;
        const isSelected = selectedCountries.some(c => c.entity === d.entity);

        d3.select(this)
          .attr('r', isSelected ? baseR + 4 : baseR)
          .attr('stroke', isSelected ? regionColorMap[d.region] : 'none')
          .attr('stroke-width', isSelected ? 3 : 0)
          .attr('fill', () => {
            if (isSelected) {
              return d3.color(regionColorMap[d.region]).brighter(0.5);
            }
            return activeRegions.includes(d.region)
              ? regionColorMap[d.region] || '#000'
              : '#ccc';
          });
      })
      .on('click', function (event, d) {
        if (!zoomMode) {
          event.stopPropagation();
          // const isAlreadySelected = selectedCountries.some(c => c.entity === d.entity);
          // const updated = isAlreadySelected
          //   ? selectedCountries.filter(c => c.entity !== d.entity)
          //   : [...selectedCountries, d];
          // setSelectedCountries(updated);
          const isAlreadySelected = selectedCountries.some(c => c.entity === d.entity);

if (isAlreadySelected) {
  // Deselect country
  setSelectedCountries(prev => prev.filter(c => c.entity !== d.entity));
} else {
  // Select country only if less than 3 selected
  if (selectedCountries.length < 3) {
    setSelectedCountries(prev => [...prev, d]);
  } else {
    alert('You can only compare up to 3 countries.');
  }
}

        }
      });

    // Labels
    chart.selectAll('text.label').remove();
    chart.selectAll('text.label')
      .data(filteredData.filter(d =>
        activeRegions.includes(d.region) ||
        selectedCountries.some(c => c.entity === d.entity)
      ))
      .enter()
      .append('text')
      .attr('class', 'label')
      .attr('x', d => x(d.gdp_per_capita))
      .attr('y', d => y(d.co2_emissions_per_capita) - 8)
      .text(d => d.entity)
      .attr('font-size', '10px')
      .attr('fill', d => regionColorMap[d.region] || '#333')
      .attr('font-weight', 'bold')
      .attr('text-anchor', 'middle');

    // Only enable brush if zoom mode is on
    if (zoomMode) {
      const brush = d3.brush()
        .extent([[0, 0], [innerWidth, innerHeight]])
        .on("end", ({ selection }) => {
          if (selection) {
            setZoomExtent(selection);
          }
        });

      chart.append("g")
        .attr("class", "brush")
        .call(brush);
    }

  }, [data, selectedYear, activeRegions, regionColorMap, selectedCountries, zoomExtent, zoomMode]);

  return (
    <>
      <svg ref={svgRef}></svg>

      {/* { !zoomMode && (
        <div style={{
          position: 'absolute',
          left: `${hoverPos.x}px`,
          top: `${hoverPos.y}px`,
          background: '#fff',
          padding: '10px',
          border: '1px solid #ccc',
          borderRadius: '6px',
          boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
          zIndex: 100,
          pointerEvents: 'none'
        }}>
          <strong>{hoveredCountry.entity}</strong><br />
          Year: {hoveredCountry.year}<br />
          GDP per capita: ${Math.round(hoveredCountry.gdp_per_capita)}<br />
          CO₂ per capita: {hoveredCountry.co2_emissions_per_capita.toFixed(2)} t<br />
          Population: {hoveredCountry.population.toLocaleString()}
        </div>
      )} */}

      {zoomExtent && (
        <div style={{ marginTop: '10px' }}>
          <button onClick={() => setZoomExtent(null)}>🔄 Reset Zoom</button>
        </div>
      )}
    </>
  );
};

export default ScatterPlot;
