import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ScatterPlot from './ScatterPlot';
import * as d3 from 'd3';

const regionList = ['Africa', 'Asia', 'Europe', 'North America', 'Oceania', 'South America'];




const regionColorMap = {};
regionList.forEach((region, i) => {
  regionColorMap[region] = d3.schemeTableau10[i]; // Use d3 color scheme
});



const Home = () => {
  const [data, setData] = useState([]);
  const [selectedYear, setSelectedYear] = useState(2022);
  const [yearRange, setYearRange] = useState({ min: 1950, max: 2022 });
  const [activeRegions, setActiveRegions] = useState([]); // All regions visible initially

  const [isPlaying, setIsPlaying] = useState(false);
  const [intervalId, setIntervalId] = useState(null);
  const [selectedCountry, setSelectedCountry] = useState(null);



  const startAnimation = () => {
    if (!isPlaying) {
      const id = setInterval(() => {
        setSelectedYear(prev => {
          const nextYear = prev + 1;
          return nextYear > yearRange.max ? yearRange.min : nextYear;
        });
      }, 100); // change speed if needed
      setIntervalId(id);
      setIsPlaying(true);
    }
  };
  
  const stopAnimation = () => {
    clearInterval(intervalId);
    setIsPlaying(false);
  };
  
  const toggleAnimation = () => {
    isPlaying ? stopAnimation() : startAnimation();
  };
  
  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then(response => {
        setData(response.data);

        const years = response.data.map(d => d.year);
        const minYear = 1800;
        const maxYear = 2022;
        setYearRange({ min: minYear, max: maxYear });
      })
      .catch(error => console.error('Error fetching data:', error));

      return () => {
        clearInterval(intervalId);
      };
  }, []);

  // Toggle region visibility
  const toggleRegion = (region) => {
    setActiveRegions(prev =>
      prev.includes(region)
        ? prev.filter(r => r !== region)
        : [...prev, region]
    );
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>GDP vs Pollution Visualization</h1>

      <div style={{ maxWidth: '1100px', margin: '0 auto', display: 'flex' }}>
  {/* Chart */}
  <div style={{ flex: 1 }}>
    <ScatterPlot
      data={data}
      selectedYear={selectedYear}
      regionColorMap={regionColorMap}
      activeRegions={activeRegions}
      selectedCountry={selectedCountry}
  setSelectedCountry={setSelectedCountry}
    />
  </div>

  {/* Legend */}
  <div style={{ marginLeft: '30px', width: '180px' }}>
    <h4 style={{ marginBottom: '12px' }}>Regions</h4>
    <form>
    {regionList.map(region => (
  <label key={region} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px', cursor: 'pointer' }}>
    <div
      onClick={() => toggleRegion(region)}
      style={{
        width: '18px',
        height: '18px',
        borderRadius: '4px',
        backgroundColor: activeRegions.includes(region) ? regionColorMap[region] : '#fff',
        border: `2px solid ${regionColorMap[region]}`,
        marginRight: '8px',
        transition: 'background-color 0.3s',
      }}
    ></div>
    <span>{region}</span>
  </label>
))}

    </form>
  </div>
</div>


   

<div style={{ marginBottom: '30px' }}>
        <label htmlFor="yearSlider">Year: {selectedYear}</label><br />
        <input
          id="yearSlider"
          type="range"
          min={yearRange.min}
          max={yearRange.max}
          value={selectedYear}
          onChange={(e) => setSelectedYear(parseInt(e.target.value))}
          style={{ width: '400px' }}
        />
      </div>

      <div style={{ marginBottom: '20px' }}>
  <button onClick={toggleAnimation}>
    {isPlaying ? 'Pause ⏸️' : 'Play ▶️'}
  </button>
</div>

    </div>
  );
};

export default Home;
