// export default ScatterPlot;
import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const ScatterPlot = ({
  data,
  selectedYear = 2022,
  regionColorMap,
  activeRegions,
  selectedCountries,
  setSelectedCountries
}) => {
  const svgRef = useRef();
  const [hoveredCountry, setHoveredCountry] = useState(null);
  const [hoverPos, setHoverPos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (!data.length) return;

    const filteredData = data.filter(d =>
      d.year === selectedYear &&
      d.entity !== 'World' &&
      !d.entity.toLowerCase().includes('income') &&
      d.gdp_per_capita > 0 &&
      d.co2_emissions_per_capita > 0 &&
      d.population > 0
    );

    const width = 900;
    const height = 550;
    const margin = { top: 40, right: 40, bottom: 60, left: 80 };

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const chart = svg
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    // const xScale = d3.scaleLog()
    //   .domain([100, d3.max(filteredData, d => d.gdp_per_capita || 100000)])
    //   .range([0, innerWidth]);

    // const yScale = d3.scaleLog()
    //   .domain([0.1, d3.max(filteredData, d => d.co2_emissions_per_capita || 10)])
    //   .range([innerHeight, 0]);

    // Filtered data based on active region (for axis zooming)
const visibleData = activeRegions.length > 0
  ? filteredData.filter(d => activeRegions.includes(d.region))
  : filteredData;

// X & Y axis scale domains based on visible data
const xMin = d3.min(visibleData, d => d.gdp_per_capita || 100);
const xMax = d3.max(visibleData, d => d.gdp_per_capita || 100000);
const yMin = d3.min(visibleData, d => d.co2_emissions_per_capita || 0.1);
const yMax = d3.max(visibleData, d => d.co2_emissions_per_capita || 10);

const xScale = d3.scaleLog()
  .domain([Math.max(100, xMin), xMax])
  .range([0, innerWidth])
  .nice();

const yScale = d3.scaleLog()
  .domain([Math.max(0.1, yMin), yMax])
  .range([innerHeight, 0])
  .nice();


    chart.append('g')
      .attr('transform', `translate(0, ${innerHeight})`)
      .call(d3.axisBottom(xScale).ticks(10, "~s"))
      .append('text')
      .attr('x', innerWidth / 2)
      .attr('y', 40)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('GDP per capita (log scale)');

    chart.append('g')
      .call(d3.axisLeft(yScale).ticks(10, "~s"))
      .append('text')
      .attr('transform', 'rotate(-90)')
      .attr('x', -innerHeight / 2)
      .attr('y', -60)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('CO₂ emissions per capita (log scale)');

    // RING CIRCLES
    chart.selectAll('circle.ring')
      .data(filteredData.filter(d => activeRegions.includes(d.region)))
      .enter()
      .append('circle')
      .attr('class', 'ring')
      .attr('cx', d => xScale(d.gdp_per_capita || 100))
      .attr('cy', d => yScale(d.co2_emissions_per_capita || 0.1))
      .attr('r', d => Math.sqrt(d.population) / 1000 + 4)
      .attr('fill', 'none')
      .attr('stroke', d => regionColorMap[d.region] || '#000')
      .attr('stroke-width', 2)
      .attr('opacity', 1);

    // MAIN CIRCLES
    chart.selectAll('circle.bubble')
      .data(filteredData)
      .enter()
      .append('circle')
      .attr('class', 'bubble')
      .attr('cx', d => xScale(d.gdp_per_capita || 100))
      .attr('cy', d => yScale(d.co2_emissions_per_capita || 0.1))
      .attr('r', d => {
        const baseR = Math.sqrt(d.population) / 1000;
        return selectedCountries.some(c => c.entity === d.entity) ? baseR + 4 : baseR;
      })
      .attr('fill', d => {
        if (selectedCountries.some(c => c.entity === d.entity)) {
          return d3.color(regionColorMap[d.region]).brighter(0.5);
        }
        return activeRegions.includes(d.region)
          ? regionColorMap[d.region] || '#000'
          : '#ccc';
      })
      .attr('stroke', d =>
        selectedCountries.some(c => c.entity === d.entity)
          ? regionColorMap[d.region]
          : 'none'
      )
      .attr('stroke-width', d =>
        selectedCountries.some(c => c.entity === d.entity) ? 3 : 0
      )
      .attr('opacity', 0.85)
      .on('mouseover', function (event, d) {
        setHoveredCountry(d);
        setHoverPos({ x: event.pageX + 10, y: event.pageY - 28 });

        d3.select(this)
          .attr('stroke', regionColorMap[d.region] || '#000')
          .attr('stroke-width', 3)
          .attr('fill', d3.color(regionColorMap[d.region]).brighter(0.5))
          .attr('r', Math.sqrt(d.population) / 1000 + 4);
      })
      .on('mouseout', function (event, d) {
        setHoveredCountry(null);

        const baseR = Math.sqrt(d.population) / 1000;
        const isSelected = selectedCountries.some(c => c.entity === d.entity);

        d3.select(this)
          .attr('r', isSelected ? baseR + 4 : baseR)
          .attr('stroke', isSelected ? regionColorMap[d.region] : 'none')
          .attr('stroke-width', isSelected ? 3 : 0)
          .attr('fill', () => {
            if (isSelected) {
              return d3.color(regionColorMap[d.region]).brighter(0.5);
            }
            return activeRegions.includes(d.region)
              ? regionColorMap[d.region] || '#000'
              : '#ccc';
          });
      })
      .on('click', function (event, d) {
        event.stopPropagation();
        const isAlreadySelected = selectedCountries.some(c => c.entity === d.entity);
        const updated = isAlreadySelected
          ? selectedCountries.filter(c => c.entity !== d.entity)
          : [...selectedCountries, d];
        setSelectedCountries(updated);
      });

    // Labels for selected countries only
    chart.selectAll('text.label').remove();

    chart.selectAll('text.label')
      .data(filteredData.filter(d =>
        activeRegions.includes(d.region) || selectedCountries.some(c => c.entity === d.entity)
      ))
      .enter()
      .append('text')
      .attr('class', 'label')
      .attr('x', d => xScale(d.gdp_per_capita || 100))
      .attr('y', d => yScale(d.co2_emissions_per_capita || 0.1) - 8)
      .text(d => d.entity)
      .attr('font-size', '10px')
      .attr('fill', d => regionColorMap[d.region] || '#333')
      .attr('font-weight', 'bold')
      .attr('text-anchor', 'middle');
  }, [data, selectedYear, activeRegions, regionColorMap, selectedCountries]);

  return (
    <>
      <svg ref={svgRef}></svg>

      {/* Hover-only tooltip card */}
      {hoveredCountry && (
        <div style={{
          position: 'absolute',
          left: `${hoverPos.x}px`,
          top: `${hoverPos.y}px`,
          background: '#fff',
          padding: '10px',
          border: '1px solid #ccc',
          borderRadius: '6px',
          boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
          zIndex: 100,
          pointerEvents: 'none'
        }}>
          <strong>{hoveredCountry.entity}</strong><br />
          Year: {hoveredCountry.year}<br />
          GDP per capita: ${Math.round(hoveredCountry.gdp_per_capita)}<br />
          CO₂ per capita: {hoveredCountry.co2_emissions_per_capita.toFixed(2)} t<br />
          Population: {hoveredCountry.population.toLocaleString()}
        </div>
      )}
    </>
  );
};

export default ScatterPlot;
