import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const ScatterPlot = ({
  data,
  selectedYear = 2022,
  regionColorMap,
  activeRegions,
  selectedCountries,
  setSelectedCountries
}) => {
  const svgRef = useRef();
  const [tooltipPos, setTooltipPos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (!data.length) return;

    const filteredData = data.filter(d =>
      d.year === selectedYear &&
      d.entity !== 'World' &&
      !d.entity.toLowerCase().includes('income') &&
      d.gdp_per_capita > 0 &&
      d.co2_emissions_per_capita > 0 &&
      d.population > 0
    );

    const width = 900;
    const height = 550;
    const margin = { top: 40, right: 40, bottom: 60, left: 80 };

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove(); // Clear old content

    const chart = svg
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    const xScale = d3.scaleLog()
      .domain([100, d3.max(filteredData, d => d.gdp_per_capita || 100000)])
      .range([0, innerWidth]);

    const yScale = d3.scaleLog()
      .domain([0.1, d3.max(filteredData, d => d.co2_emissions_per_capita || 10)])
      .range([innerHeight, 0]);

    chart.append('g')
      .attr('transform', `translate(0, ${innerHeight})`)
      .call(d3.axisBottom(xScale).ticks(10, "~s"))
      .append('text')
      .attr('x', innerWidth / 2)
      .attr('y', 40)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('GDP per capita (log scale)');

    chart.append('g')
      .call(d3.axisLeft(yScale).ticks(10, "~s"))
      .append('text')
      .attr('transform', 'rotate(-90)')
      .attr('x', -innerHeight / 2)
      .attr('y', -60)
      .attr('fill', '#000')
      .attr('text-anchor', 'middle')
      .text('CO₂ emissions per capita (log scale)');

    // RING CIRCLES (outer)
    chart.selectAll('circle.ring')
      .data(filteredData.filter(d => activeRegions.includes(d.region)))
      .enter()
      .append('circle')
      .attr('class', 'ring')
      .attr('cx', d => xScale(d.gdp_per_capita || 100))
      .attr('cy', d => yScale(d.co2_emissions_per_capita || 0.1))
      .attr('r', d => Math.sqrt(d.population) / 1000 + 4)
      .attr('fill', 'none')
      .attr('stroke', d => regionColorMap[d.region] || '#000')
      .attr('stroke-width', 2)
      .attr('opacity', 1);

    // MAIN CIRCLES
    chart.selectAll('circle.bubble')
      .data(filteredData)
      .enter()
      .append('circle')
      .attr('class', 'bubble')
      .attr('cx', d => xScale(d.gdp_per_capita || 100))
      .attr('cy', d => yScale(d.co2_emissions_per_capita || 0.1))
      .attr('r', d => {
        const baseR = Math.sqrt(d.population) / 1000;
        return selectedCountry?.entity === d.entity ? baseR + 4 : baseR;
      })
      .attr('fill', d => {
        if (selectedCountry?.entity === d.entity) {
          return d3.color(regionColorMap[d.region]).brighter(0.5);
        }
        return activeRegions.includes(d.region)
          ? regionColorMap[d.region] || '#000'
          : '#ccc';
      })
      .attr('stroke', d =>
        selectedCountry?.entity === d.entity ? regionColorMap[d.region] : 'none'
      )
      .attr('stroke-width', d =>
        selectedCountry?.entity === d.entity ? 3 : 0
      )
      .attr('opacity', 0.85)
      .on('mouseover', function (event, d) {
        d3.select(this)
          .attr('stroke', regionColorMap[d.region] || '#000')
          .attr('stroke-width', 3)
          .attr('fill', d3.color(regionColorMap[d.region]).brighter(0.5))
          .attr('r', Math.sqrt(d.population) / 1000 + 4);
      })
      .on('mouseout', function (event, d) {
        const baseR = Math.sqrt(d.population) / 1000;
        d3.select(this)
          .attr('r', selectedCountry?.entity === d.entity ? baseR + 4 : baseR)
          .attr('stroke', selectedCountry?.entity === d.entity ? regionColorMap[d.region] : 'none')
          .attr('stroke-width', selectedCountry?.entity === d.entity ? 3 : 0)
          .attr('fill', d => {
            if (selectedCountry?.entity === d.entity) {
              return d3.color(regionColorMap[d.region]).brighter(0.5);
            }
            return activeRegions.includes(d.region)
              ? regionColorMap[d.region] || '#000'
              : '#ccc';
          });
      })
      .on('click', function (event, d) {
        event.stopPropagation();
        setTooltipPos({ x: event.pageX + 10, y: event.pageY - 28 });
        setSelectedCountry(d);
      });

    // Labels
    chart.selectAll('text.label').remove();
    chart.selectAll('text.label')
      .data(filteredData.filter(d => activeRegions.includes(d.region)))
      .enter()
      .append('text')
      .attr('class', 'label')
      .attr('x', d => xScale(d.gdp_per_capita || 100))
      .attr('y', d => yScale(d.co2_emissions_per_capita || 0.1) - 8)
      .text(d => d.entity)
      .attr('font-size', '10px')
      .attr('fill', d => regionColorMap[d.region] || '#333')
      .attr('font-weight', 'bold')
      .attr('text-anchor', 'middle');
  }, [data, selectedYear, activeRegions, regionColorMap, selectedCountry]);

  // Deselect on outside click
  useEffect(() => {
    const handleClickOutside = () => {
      setSelectedCountry(null);
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [setSelectedCountry]);

  return (
    <>
      <svg ref={svgRef}></svg>
      {selectedCountry && (
        <div style={{
          position: 'absolute',
          left: `${tooltipPos.x}px`,
          top: `${tooltipPos.y}px`,
          background: '#fff',
          padding: '10px',
          border: '1px solid #ccc',
          borderRadius: '6px',
          boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
          zIndex: 100,
          pointerEvents: 'none'
        }}>
          <strong>{selectedCountry.entity}</strong><br />
          Year: {selectedCountry.year}<br />
          GDP per capita: ${Math.round(selectedCountry.gdp_per_capita)}<br />
          CO₂ per capita: {selectedCountry.co2_emissions_per_capita.toFixed(2)} t<br />
          Population: {selectedCountry.population.toLocaleString()}
        </div>
      )}
    </>
  );
};

export default ScatterPlot;
