import React from 'react';
import * as d3 from 'd3';

const chartWidth = 160;
const chartHeight = 80;
const margin = { top: 20, right: 10, bottom: 20, left: 35 };

const metrics = [
  { key: 'co2', label: 'CO₂ per Capita (t)' },
  { key: 'gdp', label: 'GDP per Capita ($)' },
  { key: 'population', label: 'Population' }
];

const MultiTrendCharts = ({ selectedCountries, trendMap, regionColorMap }) => {
  if (selectedCountries.length === 0) return null;

  // Get shared X scale across all charts
  const allYears = trendMap[selectedCountries[0].entity].map(d => d.year);
  const xScale = d3.scaleLinear()
    .domain(d3.extent(allYears))
    .range([margin.left, chartWidth - margin.right]);

  return (
    <div style={{ display: 'flex', gap: '30px', marginTop: '20px' }}>
      {selectedCountries.map((country) => (
        <div key={country.entity}>
          <div style={{  marginBottom: '6px', fontWeight: 'bold' }}>
            {country.entity}
          </div>

          {metrics.map((metric) => {
  const rawData = trendMap[country.entity] || [];

  // ✅ Filter only valid data (starting from 1850 and valid values)
  const data = rawData.filter(d =>
    d.year >= 1850 &&
    d[metric.key] !== null &&
    d[metric.key] !== undefined &&
    d[metric.key] > 0
  );

  if (data.length === 0) return null; // Skip if nothing to draw


            return (
              <svg
                key={metric.key}
                width={chartWidth}
                height={chartHeight}
                style={{ display: 'block', marginBottom: '10px' }}
              >
                {/* Y Axis ticks */}
                {yScale.ticks(3).map((tick, i) => (
                  <g key={i}>
                    <text
                      x={margin.left - 6}
                      y={yScale(tick)}
                      fontSize="10"
                      textAnchor="end"
                      alignmentBaseline="middle"
                      fill="#444"
                    >
                      {tick.toLocaleString()}
                    </text>
                    <line
                      x1={margin.left}
                      x2={chartWidth - margin.right}
                      y1={yScale(tick)}
                      y2={yScale(tick)}
                      stroke="#ccc"
                      strokeDasharray="2 2"
                    />
                  </g>
                ))}

                {/* Metric Label */}
                <text
                  x={chartWidth / 2}
                  y={12}
                  textAnchor="middle"
                  fontSize="11"
                  fill="#333"
                >
                  {metric.label}
                </text>

                {/* Line Path */}
                <path
                  d={line(data)}
                  fill="none"
                  stroke={regionColorMap[country.region] || '#000'}
                  strokeWidth={2}
                />
              </svg>
            );
          })}
        </div>
      ))}
    </div>
  );
};

export default MultiTrendCharts;
