import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import ScatterPlot from './ScatterPlot';
import CountrySelector from './CountrySelector';
import MiniTrendChart from './MiniTrendChart'; // ‚¨ÖÔ∏è New component (you'll create this)
import * as d3 from 'd3';

const regionList = ['Africa', 'Asia', 'Europe', 'North America', 'Oceania', 'South America'];

const regionColorMap = {};
regionList.forEach((region, i) => {
  regionColorMap[region] = d3.schemeTableau10[i];
});

const Home = () => {
  const [data, setData] = useState([]);
  const [selectedYear, setSelectedYear] = useState(2022);
  const [yearRange, setYearRange] = useState({ min: 1950, max: 2022 });
  const [activeRegions, setActiveRegions] = useState([]);
  const [selectedCountries, setSelectedCountries] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [intervalId, setIntervalId] = useState(null);
  const [showCountrySelector, setShowCountrySelector] = useState(false);
  const [zoomMode, setZoomMode] = useState(false);

  // ‚¨áÔ∏è NEW STATE
  const [hoveredCountry, setHoveredCountry] = useState(null);
  const [hoverTrendData, setHoverTrendData] = useState([]);

  // ‚¨áÔ∏è Build country trend map on data load
  const countryTrendMap = useMemo(() => {
    const map = {};
    data.forEach(d => {
      if (!map[d.entity]) map[d.entity] = [];
      map[d.entity].push({ year: d.year, co2: d.co2_emissions_per_capita });
    });
    return map;
  }, [data]);

  const startAnimation = () => {
    if (!isPlaying) {
      const id = setInterval(() => {
        setSelectedYear(prev => {
          const nextYear = prev + 1;
          if (nextYear > yearRange.max) {
            clearInterval(id);
            setIsPlaying(false);
            return yearRange.max;
          }
          return nextYear;
        });
      }, 100);
      setIntervalId(id);
      setIsPlaying(true);
    }
  };

  const stopAnimation = () => {
    clearInterval(intervalId);
    setIsPlaying(false);
  };

  const toggleAnimation = () => {
    isPlaying ? stopAnimation() : startAnimation();
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then(response => {
        setData(response.data);
        setYearRange({ min: 1800, max: 2022 });
      })
      .catch(error => console.error('Error fetching data:', error));

    return () => clearInterval(intervalId);
  }, []);

  const toggleRegion = (region) => {
    setActiveRegions(prev =>
      prev.includes(region)
        ? prev.filter(r => r !== region)
        : [...prev, region]
    );
  };

  const toggleCountry = (country) => {
    const exists = selectedCountries.find(c => c.entity === country.entity);
    if (exists) {
      setSelectedCountries(prev => prev.filter(c => c.entity !== country.entity));
    } else {
      setSelectedCountries(prev => [...prev, country]);
    }
  };

  return (
    <div style={{ padding: '20px', position: 'relative' }}>
      <h1>GDP vs Pollution Visualization</h1>

      {/* Zoom Toggle */}
      <div style={{ marginBottom: '12px', display: 'flex', gap: '10px', alignItems: 'center' }}>
        <button
          onClick={() => setZoomMode(!zoomMode)}
          style={{
            padding: '8px 12px',
            backgroundColor: zoomMode ? '#007bff' : '#ccc',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {zoomMode ? 'üß≠ Zoom Mode: ON' : 'üîç Enable Zoom Mode'}
        </button>
      </div>

      <div style={{ maxWidth: '1100px', margin: '0 auto', display: 'flex' }}>
        {/* Chart */}
        <div style={{ flex: 1 }}>
          <ScatterPlot
            data={data}
            selectedYear={selectedYear}
            regionColorMap={regionColorMap}
            activeRegions={activeRegions}
            selectedCountries={selectedCountries}
            setSelectedCountries={setSelectedCountries}
            zoomMode={zoomMode}
            setHoveredCountry={setHoveredCountry}
            setHoverTrendData={(entity) => setHoverTrendData(countryTrendMap[entity] || [])}
          />
          

          {/* Timeline */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '800px',
            margin: '30px auto 20px auto',
            gap: '10px'
          }}>
            <button
              onClick={toggleAnimation}
              style={{
                width: '40px',
                padding: '8px',
                fontSize: '14px',
                color: 'grey',
                border: '1px solid #999',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              {isPlaying ? '‚ùö‚ùö' : '‚ñ∂'}
            </button>

            <input
              type="range"
              min={yearRange.min}
              max={yearRange.max}
              value={selectedYear}
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
              style={{
                flex: 1,
                height: '6px',
                background: '#ccc',
                borderRadius: '3px',
                appearance: 'none',
                WebkitAppearance: 'none',
                outline: 'none',
                cursor: 'pointer',
                accentColor: 'grey'
              }}
            />
          </div>
        </div>

        {/* Right Panel: Region Legend + Mini Chart */}
        <div style={{ marginLeft: '30px', width: '180px' }}>
          <h4 style={{ marginBottom: '12px' }}>Regions</h4>
          <form>
            {regionList.map(region => (
              <label key={region} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px', cursor: 'pointer' }}>
                <input
                  type="checkbox"
                  checked={activeRegions.includes(region)}
                  onChange={() => toggleRegion(region)}
                  style={{
                    appearance: 'none',
                    WebkitAppearance: 'none',
                    width: '18px',
                    height: '18px',
                    borderRadius: '4px',
                    backgroundColor: activeRegions.includes(region)
                      ? regionColorMap[region]
                      : '#fff',
                    border: `2px solid ${regionColorMap[region]}`,
                    marginRight: '8px',
                    cursor: 'pointer'
                  }}
                />
                <span>{region}</span>
              </label>
            ))}
          </form>

          {/* Country selector + reset */}
          <div style={{ marginTop: '20px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
            <button onClick={() => setShowCountrySelector(true)}>+ Select Countries</button>
            <button
              onClick={() => {
                setSelectedCountries([]);
                setActiveRegions([]);
              }}
              style={{
                backgroundColor: '#f44336',
                color: 'white',
                border: 'none',
                padding: '8px',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Reset Selections
            </button>
          </div>

          {/* ‚¨áÔ∏è Mini Trend Chart Below Region Legend */}
          {hoveredCountry && hoverTrendData.length > 0 && (
            <div style={{ marginTop: '30px' }}>
              <MiniTrendChart
                country={hoveredCountry.entity}
                data={hoverTrendData}
                color={regionColorMap[hoveredCountry.region] || '#000'}
              />
            </div>
          )}
        </div>
      </div>

      <div style={{ textAlign: 'center', fontSize: '16px', marginBottom: '10px', color: '#444' }}>
        Year: {selectedYear}
      </div>

      {/* Country Selector */}
      {showCountrySelector && (
        <CountrySelector
          data={data}
          selectedCountries={selectedCountries}
          toggleCountry={toggleCountry}
          close={() => setShowCountrySelector(false)}
        />
      )}
    </div>
  );
};

export default Home;
