import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then((response) => {
        console.log('Fetched Data:', response.data); // Debugging: Check API response
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>GDP vs Pollution Visualization</h1>
      <p>Explore the relationship between GDP per capita and CO₂ emissions per capita using interactive charts.</p>

      {/* Simple List View of Fetched Data */}
      <h3>Fetched Data (First 5 Entries):</h3>
      <ul>
        {data.slice(0, 5).map((item, index) => (
          <li key={index}>
            {item.entity} ({item.year}): GDP - {item.gdp_per_capita}, CO₂ - {item.co2_emissions_per_capita}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
